import React from 'react'

const Book = ({book,changeShelf}) => {

    const updateShelf = (event) =>{
        event.preventDefault();
        const change = changeShelf(book, event.target.value);
    };
    const [selected, setSelected] = useState('');
    const isShelfUndefined = shelf === undefined;
  
    useState(() => {
      isShelfUndefined ? setSelected('none') : setSelected(shelf);
    });
  
  return (
    <li>
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{width: 128, height: 188, backgroundImage:`url(${book.imageLinks.smallThumbnail})`,}}
        ></div>
        <div className="book-shelf-changer">
        <select onChange={updateShelf} value={book.shelf}>
            <option value="none" disabled>Move to...</option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.title}</div>
      <div className="book-authors">{book.authors}</div>
    </div>
  </li>
    )
}

export default Book
