import React from 'react'
import { useState } from 'react';
import * as BooksAPI from './BooksAPI'
import {useNavigate} from "react-router-dom";

const Book = ({book,onBookUpdate}) => {
    const changeShelf = (option) => {
        onBookUpdate(bookData, option);
      };
    
      
    const [selected, setSelected] = useState('');
      
    const UpdateShelf = (event) =>{
        const isShelfUndefined = event.target.value === undefined;
    
        setSelected(event.target.value);
        changeShelf(event.target.value);
    };
  
  return (
    <li>
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{width: 128, height: 188, backgroundImage:`url(${book.imageLinks.smallThumbnail})`,}}
        ></div>
        <div className="book-shelf-changer">
        <select onChange={UpdateShelf} value={selected}>
            <option value="none" disabled>Move to...</option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.title}</div>
      <div className="book-authors">{book.authors}</div>
    </div>
  </li>
    )
}

export default Book
