import React from 'react'
import { useState } from 'react';
import * as BooksAPI from './BooksAPI'
import {useNavigate} from "react-router-dom";

const Book = ({book}) => {
    const [books, setBooks] = useState([]);
    let navigate = useNavigate();

    const changeShelf = (book, shelf) => {
        const updateBooks = async () => {
          await BooksAPI.update(book, shelf);
          const response = await BooksAPI.getAll();
          setBooks(response);
        };
        updateBooks();
        navigate('/');
      };
      
      const [selected, setSelected] = useState('');
      const isShelfUndefined = book.shelf === undefined;
      
      
      const UpdateShelf = (event) =>{
        useState(() => {
          isShelfUndefined ? setSelected('none') : setSelected(book.shelf);
        });
        event.preventDefault();
        setSelected(event.target.value);
        changeShelf(book, event.target.value);
    };
  
  return (
    <li>
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{width: 128, height: 188, backgroundImage:`url(${book.imageLinks.smallThumbnail})`,}}
        ></div>
        <div className="book-shelf-changer">
        <select onChange={UpdateShelf} value={selected}>
            <option value="none" disabled>Move to...</option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.title}</div>
      <div className="book-authors">{book.authors}</div>
    </div>
  </li>
    )
}

export default Book
